import { createClient } from '@supabase/supabase-js';
import type { Database } from './types';

// Use environment variables for Supabase configuration
const SUPABASE_URL = import.meta.env.VITE_SUPABASE_URL;
const SUPABASE_ANON_KEY = import.meta.env.VITE_SUPABASE_ANON_KEY;

// Debug logging
console.log('üîç DEBUG: SUPABASE_URL from env:', SUPABASE_URL ? 'FOUND' : 'NOT FOUND');
console.log('üîç DEBUG: SUPABASE_ANON_KEY from env:', SUPABASE_ANON_KEY ? 'FOUND' : 'NOT FOUND');

// Validate environment variables
if (!SUPABASE_URL || !SUPABASE_ANON_KEY) {
  console.error('‚ùå ERROR: Missing Supabase environment variables');
  console.error('Please set VITE_SUPABASE_URL and VITE_SUPABASE_ANON_KEY in your .env file');
  throw new Error('Missing Supabase configuration. Check your environment variables.');
}

// Import the supabase client like this:
// import { supabase } from "@/integrations/supabase/client";

export const supabase = createClient<Database>(
  SUPABASE_URL, 
  SUPABASE_ANON_KEY, 
  {
    auth: {
      persistSession: true,
      autoRefreshToken: true,
      detectSessionInUrl: true
    },
    db: {
      schema: 'public'
    }
  }
);

// Debug logging for the actual URL being used
console.log('üîç DEBUG: Final SUPABASE_URL being used:', supabase.supabaseUrl);

// Test database connection
export const testConnection = async () => {
  try {
    const { data, error } = await supabase
      .from('profiles')
      .select('id')
      .limit(1);
    
    if (error) {
      console.error('Database connection test failed:', error);
      return false;
    }
    
    console.log('‚úÖ Database connection successful');
    return true;
  } catch (error) {
    console.error('‚ùå Database connection test error:', error);
    return false;
  }
};
